import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import Swal from 'sweetalert2';
import './ProductDetail.css';

const products = [
  { id: 1, name: 'Vestido estampado Viscose', price: 'R$379', image: 'https://via.placeholder.com/150' },
  { id: 2, name: 'Camisata CLASSIC Algodão', price: 'R$147', image: 'https://via.placeholder.com/150' },
  { id: 3, name: 'Camisa de Linho de manga longa', price: 'R$83,90', image: 'https://via.placeholder.com/150' },
  { id: 4, name: 'Vestido em Liocel Azul FREE', price: 'R$159,90', image: 'https://via.placeholder.com/150' },
  { id: 5, name: 'Vestido Preto tubo Poliamida', price: 'R$85,90', image: 'https://via.placeholder.com/150' },
  { id: 6, name: 'Jaqueta Retro Reciclado Poliéster', price: 'R$1.542', image: 'https://via.placeholder.com/150' },
];

function ProductDetail() {
  const { id } = useParams();
  const product = products.find(p => p.id === parseInt(id));
  const [cart, setCart] = useState([]);

  const handleAddToCart = () => {
    setCart([...cart, product]);
    Swal.fire({
      title: 'Adicionado ao carrinho!',
      text: `${product.name} foi adicionado ao carrinho.`,
      icon: 'success',
      confirmButtonText: 'OK'
    });
  };

  if (!product) {
    return <h2>Oops... Produto não encontrado :/ </h2>;
  }

  return (
    <div className="product-detail">
      <div className="product-detail-image">
        <img src={product.image} alt={product.name} />
      </div>
      <div className="product-detail-info">
        <h2>{product.name}</h2>
        <p>{product.price}</p>
        <button className="buy-button" onClick={handleAddToCart}>Comprar</button>
        <div className='product-about'>
          <h3>Sobre</h3>
          <p>Et reprehenderit eveniet ut harum illo sit voluptas exercitationem sed quam debitis ab laudantium totam. At dolorum veritatis qui accusamus minima et ipsam neque sit Quis odio et omnis perspiciatis quo sint perspiciatis! Aut soluta perferendis et incidunt neque qui rerum commodi est doloremque atque eos voluptas delectus sit impedit quae. Est voluptatibus recusandae sit dolor expedita est quae esse in nesciunt quibusdam.</p>
        </div>
        {/* Adicionar useState ao Comentário */}
        <div className='product-comentary'>
          <h3>Comentários</h3>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
