import React from 'react';
import { useCart } from './CartContext';
import './Cart.css';

function Cart() {
  const { cart } = useCart();

  if (cart.length === 0) {
    return <h2>O carrinho est√° vazio.</h2>;
  }

  return (
    <div className="cart">
      <h2>Seu Carrinho</h2>
      <ul>
        {cart.map((product, index) => (
          <li key={index}>
            <img src={product.image} alt={product.name} />
            <div>
              <h3>{product.name}</h3>
              <p>{product.price}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Cart;
